var amount = current.u_amount;
gs.info('Amount ' + amount);
var approvers = [];
approvers = getApprovers();
gs.info('approvers ' + approvers);
workflow.scratchpad.approvers = approvers;
gs.info('wf scratchpad applist ' + workflow.scratchpad.approvers);
workflow.scratchpad.current_approver = 0;  //used as an index for the array
workflow.scratchpad.arrLength = approvers.length; //used to check if another approval is needed.

//Get the approvers
function getApprovers() {
	var appList = [];
	var gr = new GlideRecord('x_ahho_finance_ar_approval_matrix');
	gr.addQuery('amount', '<', amount);
	gr.orderBy('level');
	gr.query();
	while(gr.next()) {
		appList.push(gr.getValue('approver'));
	}
	return appList;
}

